mapper.select:
select goods_no, goods_name, price, discount, discount_rate, saved_rate, image_name from ( select rownum rnum, goods_no, goods_name, price, discount, discount_rate, saved_rate, image_name from ( select g.goods_no, g.goods_name, p.price, p.discount, p.discount_rate, p.saved_rate, g.image_name from goods g, goods_price p where (
sale_start_date <= sysDate and trunc(sysDate) <= sale_end_date
) and (
g.goods_no = p.goods_no ) ) ) where rnum between #{pageObject.startRow} and #{pageObject.endRow}

mapper.insert:
insert into goods_price (goods_price_no, price, discount, discount_rate, saved_rate, delivery_charge
, sale_start_date
, sale_end_date
, goods_no) values (goods_price_seq.nextval, #{price}, #{discount,jdbcType=INTEGER}, #{discount_rate,jdbcType=INTEGER}, #{saved_rate,jdbcType=INTEGER}, #{delivery_charge,jdbcType=INTEGER}
, #{sale_start_date,jdbcType=DATE}
, #{sale_end_date,jdbcType=DATE}
, #{goods_no})

insert into goods_color (goods_color_no, color_name, goods_no) select goods_color_seq.nextval, color_name, goods_no from (
select #{vo.color_name} color_name, #{vo.goods_no} goods_no from dual
)
